mapper.select:
SELECT *
FROM(
    SELECT rownum rn
        , r.*
    FROM(
        SELECT *
        FROM productReview
        WHERE pno=#{pno}
        order by rnum desc
    )r
)
WHERE rn
    BETWEEN (#{cri.pageNum} - 1) * #{cri.amount} + 1 AND #{cri.pageNum} * #{cri.amount}

SELECT count(*)
FROM productReview
WHERE pno=#{pno}

SELECT count(*)
FROM productQna
WHERE pno=#{pno}

SELECT count(*)
FROM likeProduct
WHERE userId=#{userId}
    AND pno=#{pno}

mapper.insert:
INSERT INTO cart(
    cartNo
    , userId
    , ckId
    , createdAt
    , updatedAt
)
VALUES(
    #{cartNo}
    , #{userId}
    , #{ckId}
    , sysdate
    , sysdate
)

update.foreach:
UPDATE cartDetail SET
cCount = cCount + #{item.cCount}
, cPrice = cPrice + #{item.cPrice}
WHERE
pOpNo = #{item.pOpNo}
AND
cartNo = #{item.cartNo}

insert.foreach:
INTO cartDetail(
    cdNo
    , cartNo
    , pOpNo
    , cCount
    , cPrice
)
VALUES(
    #{item.cdNo}
    , #{item.cartNo}
    , #{item.pOpNo}
    , #{item.cCount}
    , #{item.cPrice}
)

mapper.insert:
INSERT INTO productQna(
    pQnANo
    , pno
    , userId
    , pQnAContent
    , pQnARegDate
    , pQnAGroupNo
)
values(
    product_QnA_SEQ.NEXTVAL
    , #{pno}
    , #{userId}
    , #{pQnAContent}
    , sysdate
    , product_QnA_SEQ.NEXTVAL
)

INSERT INTO likeProduct(
    likeNo
    , userId
    , pno
)
VALUES(
    #{likeNo}
    , #{userId}
    , #{pno}
)

mapper.delete:
DELETE FROM likeProduct
WHERE userId=#{userId}
    AND pno=#{pno}
